import React, { Component } from 'react';
import './App.css';
import api from './api';

class App extends Component{

  state= {
    solicitations: [],
  } 

  async componentDidMount(){
    const response = await api.get('http://localhost:5000/solicitations/');
    //console.log(response.data);

    this.setState({ solicitations: response.data })
  }

  async postData(){
    try {

      //let result = await fetch();

      let result = await fetch('http://localhost:5000/solicitations/', {
        method: 'post',
        headers: {
          'Accept': 'application/json',
          'Content-type': 'application/json',
        },
        body: JSON.stringify({
          client: 'jake',
          address: 'petrolina',
          income: 3000,
        })
      });

      console.log(result)

    } catch (e) {
      console.log(e)
    }
  }

  render(){

    const {solicitations} = this.state;

    return(
      <div>
        <div class="row">
            <table class="table">
             <thead>
              <tr>
                <th scope="col">#</th>
                <th scope="col">Cliente</th>
                <th scope="col">Endereço</th>
                <th scope="col">Renda</th>
                <th scope="col">Score</th>
                <th scope="col">Status</th>
                <th scope="col">Crédito</th>
              </tr>
            </thead>
              {solicitations.map(solicitation =>(  
                  <tbody>
                    <tr>
                      <th scope="row">{ solicitation.id }</th>
                      <td> { solicitation.client }</td>
                      <td> { solicitation.address }</td>
                      <td>R$ { solicitation.income }</td>
                      <td> { solicitation.score } </td>
                      <td> { solicitation.status } </td>
                      <td>R$ { solicitation.credit } </td>
                    </tr>
                </tbody>
                ))}
            </table>
          </div>     
      </div>
    );
  };
};

export default App;
