import React, { Component } from 'react';
import './App.css';
import api from './api';

class App extends Component{

  state= {
    solicitations: [],
  } 

  async componentDidMount(){
    const response = await api.get('http://localhost:5000/solicitations/');
    //console.log(response.data);

    this.setState({ solicitations: response.data })
  }

  render(){

    const {solicitations} = this.state;

    return(
      <div>
      <h1>Listar solicitações</h1>
      {solicitations.map(solicitation =>(
        <li key={solicitation.id}>
            <h2>
              <strong>Cliente:</strong>
              {solicitation.client}
            </h2>
            <p>
              {solicitation.address}
            </p>
            <p>
              {solicitation.income}
            </p>
            <p>
              {solicitation.score}
            </p>
            <p>
              {solicitation.status}
            </p>
        </li>
      ))}
      </div>
    );
  };
};

export default App;
